import "date-fns";
import "./app.css";
const api = {
  key: "6f62f2fec77cc25de687f5c41173ed00 ",
  base: "https://home.openweathermap.org/data/2.5/",
};

function App() {
  const dateBuilder = (e) => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
    ];
    let day = days[e.getDay()];
    let date = e.getDate();
    let month = months[e.getMonth()];
    let year = e.getFullYear();
    return `${day} ${date} ${month}  ${year} `;
  };
  const day = new Date();
  console.log(day);
  return (
    <div>
      <main>
        <div className="container">
          <input type="text" placeholder="Search..." className="searchBar" />
        </div>
        <div>{dateBuilder(new Date())}</div>
      </main>
    </div>
  );
}

export default App;
